    /**
     * 找到A中存在，B中不存在
     * @param arry1
     * @param arry2
     */
    public void filterArray(String arry1, String arry2){
        arry1 = arry1.trim();
        arry2 = arry2.trim();
        String[] numberArr1 = arry1.split(",");
        String[] numberArr2 = arry2.split(",");

        Set<String> newArray = new HashSet<>();
        if(numberArr1.length >= numberArr2.length){
            newArray = filterArray(numberArr1, numberArr2);
        }else {
            newArray = filterArray(numberArr2, numberArr1);
        }
        System.out.println(newArray);
    }

    public Set<String> filterArray(String[] longArray, String[] shortArray){
        Set<String> newArray = new HashSet<>();
        List<String> shortList = Arrays.asList(shortArray);
        for(String number : longArray){
            if(shortList.contains(number)){
                continue;
            }
            newArray.add(number);
        }

        return newArray;
    }

    public void mergeArray(String array1, String array2){
        List<String> newArray = new ArrayList<>();
        array1 = array1.trim();
        array2 = array2.trim();
        List<String> arrayList1 = Arrays.asList(array1.split(","));
        List<String> arrayList2 = Arrays.asList(array2.split(","));
        newArray.addAll(arrayList1);
        arrayList2.forEach(array -> {
            if(!newArray.contains(array)){
                newArray.add(array);
            }
        });
        System.out.println(newArray);
    }

